import{_ as e,o as a,c as t,a as i}from"./app.8c45ae17.js";const S=JSON.parse('{"title":"HTTP \u548C HTTPS","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u7279\u70B9","slug":"\u7279\u70B9"},{"level":2,"title":"\u7F3A\u70B9","slug":"\u7F3A\u70B9"},{"level":2,"title":"\u7279\u70B9","slug":"\u7279\u70B9-1"},{"level":2,"title":"\u4F18\u70B9","slug":"\u4F18\u70B9"},{"level":2,"title":"\u7F3A\u70B9","slug":"\u7F3A\u70B9-1"}],"relativePath":"studyCn/Network/HTTPHTTPS.md"}'),l={name:"studyCn/Network/HTTPHTTPS.md"},d=i('<h1 id="http-\u548C-https" tabindex="-1">HTTP \u548C HTTPS <a class="header-anchor" href="#http-\u548C-https" aria-hidden="true">#</a></h1><h1 id="http" tabindex="-1">HTTP <a class="header-anchor" href="#http" aria-hidden="true">#</a></h1><p>HTTP \u5373\u8D85\u6587\u672C\u4F20\u8F93\u534F\u8BAE\uFF0C\u4E3B\u8981\u7528\u4E8E Web \u4E0A\u4F20\u8F93\u8D85\u5A92\u4F53\u6587\u672C\u7684\u5E95\u5C42\u534F\u8BAE\uFF0C\u7ECF\u5E38\u5728\u6D4F\u89C8\u5668\u548C\u670D\u52A1\u5668\u4E4B\u95F4\u4F20\u9012\u6570\u636E\u3002\u901A\u4FE1\u5C31\u662F\u4EE5\u7EAF\u6587\u672C\u7684\u5F62\u5F0F\u8FDB\u884C\u3002</p><h2 id="\u7279\u70B9" tabindex="-1">\u7279\u70B9 <a class="header-anchor" href="#\u7279\u70B9" aria-hidden="true">#</a></h2><ul><li><p><code>\u65E0\u8FDE\u63A5</code>\uFF1A\u6BCF\u4E00\u6B21\u8BF7\u6C42\u90FD\u8981\u8FDE\u63A5\u4E00\u6B21\uFF0C\u8BF7\u6C42\u7ED3\u675F\u5C31\u4F1A\u65AD\u6389\uFF0C\u4E0D\u4F1A\u4FDD\u6301\u8FDE\u63A5</p></li><li><p><code>\u65E0\u72B6\u6001</code>\uFF1A\u6BCF\u4E00\u6B21\u8BF7\u6C42\u90FD\u662F\u72EC\u7ACB\u7684\uFF0C\u670D\u52A1\u5668\u4E0D\u4F1A\u53BB\u8BB0\u5FC6 HTTP \u7684\u72B6\u6001\uFF0C\u6240\u4EE5\u4E0D\u9700\u8981\u989D\u5916\u7684\u8D44\u6E90\u6765\u8BB0\u5F55\u72B6\u6001\u4FE1\u606F\uFF0C\u4ECE\u800C\u51CF\u5C11\u4E86\u7F51\u7EDC\u5F00\u9500\u3002\u8FD9\u65E2\u662F\u4F18\u70B9\u4E5F\u662F\u7F3A\u70B9\u3002</p></li><li><p><code>\u7075\u6D3B\u6613\u4E8E\u6269\u5C55</code>\uFF1AHTTP \u534F\u8BAE\u4E2D\u7684\u5404\u79CD\u8BF7\u6C42\u65B9\u6CD5\u3001URI / URL\u3001\u72B6\u6001\u7801\u3001\u5934\u5B57\u6BB5\u7B49\u6BCF\u4E2A\u7EC4\u6210\u90FD\u662F\u53EF\u4EE5\u7531\u5F00\u53D1\u4EBA\u5458\u81EA\u5B9A\u4E49\u548C\u6269\u5145\u7684\u3002</p></li><li><p><code>\u7B80\u5355\u5FEB\u901F</code>\uFF1A\u57FA\u672C\u62A5\u6587\u7684\u683C\u5F0F\u4E3A header + body\uFF0C\u5934\u90E8\u4FE1\u606F\u4E5F\u662F\u952E\u503C\u5BF9\u7684\u6587\u672C\u5F62\u5F0F\uFF0C\u6613\u4E8E\u7406\u89E3\u3002\u5F53\u53D1\u9001\u8BF7\u6C42\u8BBF\u95EE\u67D0\u4E2A\u8D44\u6E90\u65F6\uFF0C\u53EA\u9700\u4F20\u9001\u8BF7\u6C42\u65B9\u6CD5\u548C URL \u5C31\u53EF\u4EE5\u4E86\uFF0C\u4F7F\u7528\u7B80\u5355\u3002\u6B63\u7531\u4E8Ehttp\u534F\u8BAE\u7B80\u5355\uFF0C\u4F7F\u5F97http\u670D\u52A1\u5668\u7684\u7A0B\u5E8F\u89C4\u6A21\u5C0F\uFF0C\u56E0\u800C\u901A\u4FE1\u901F\u5EA6\u5F88\u5FEB</p></li></ul><h2 id="\u7F3A\u70B9" tabindex="-1">\u7F3A\u70B9 <a class="header-anchor" href="#\u7F3A\u70B9" aria-hidden="true">#</a></h2><ul><li><p><code>\u65E0\u72B6\u6001</code>\uFF1A\u670D\u52A1\u5668\u4E0D\u4F1A\u8BB0\u5FC6 HTTP \u7684\u72B6\u6001\uFF0C\u6240\u4EE5\u4E0D\u9700\u8981\u7533\u8BF7\u989D\u5916\u7684\u8D44\u6E90\uFF0C\u80FD\u591F\u51CF\u8F7B\u670D\u52A1\u5668\u7684\u8D1F\u62C5\u3002\u4F46\u662F\u4E5F\u5C31\u65E0\u6CD5\u533A\u5206\u591A\u4E2A\u8BF7\u6C42\u53D1\u8D77\u8005\u8EAB\u4EFD\u662F\u4E0D\u662F\u540C\u4E00\u4E2A\u5BA2\u6237\u7AEF\u7684\uFF0C\u610F\u5473\u7740\u5982\u679C\u540E\u7EED\u5904\u7406\u9700\u8981\u524D\u9762\u7684\u4FE1\u606F\uFF0C\u5219\u5B83\u5FC5\u987B\u91CD\u4F20\uFF0C\u8FD9\u6837\u53EF\u80FD\u5BFC\u81F4\u6BCF\u6B21\u8FDE\u63A5\u4F20\u9001\u7684\u6570\u636E\u91CF\u589E\u5927\u3002</p></li><li><p><code>\u660E\u6587\u4F20\u8F93</code>\uFF1A\u4F20\u8F93\u7684\u62A5\u6587 (header\u90E8\u5206) \u662F\u8089\u773C\u53EF\u89C1\u7684\uFF0C\u76F4\u63A5\u5C06\u4FE1\u606F\u66B4\u9732\u7ED9\u4E86\u5916\u754C\u3002</p></li><li><p><code>\u4E0D\u5B89\u5168</code>\uFF1A\u660E\u6587\u4F20\u8F93\u53EF\u80FD\u88AB\u7A83\u542C\u4E0D\u5B89\u5168\uFF0C\u7F3A\u5C11\u8EAB\u4EFD\u8BA4\u8BC1\u53EF\u80FD\u906D\u9047\u4F2A\u88C5\uFF0C\u7F3A\u5C11\u62A5\u6587\u5B8C\u6574\u6027\u9A8C\u8BC1\u53EF\u80FD\u906D\u5230\u7BE1\u6539\u3002</p></li></ul><h1 id="https" tabindex="-1">HTTPS <a class="header-anchor" href="#https" aria-hidden="true">#</a></h1><p>HTTPS \u5728 HTTP \u7684\u57FA\u7840\u4E0A\u6DFB\u52A0\u4E86 <code>SSL / TLS</code> \u5B89\u5168\u4F20\u8F93\u534F\u8BAE\uFF0C\u4F7F\u5F97\u62A5\u6587\u80FD\u591F\u52A0\u5BC6\u4F20\u8F93\u3002</p><p>HTTPS \u5728 TCP \u4E09\u6B21\u63E1\u624B\u4E4B\u540E\uFF0C\u8FD8\u9700\u8981\u8FDB\u884C SSL /TLS \u7684\u63E1\u624B\u8FC7\u7A0B\uFF0C\u624D\u53EF\u8FDB\u5165\u52A0\u5BC6\u62A5\u6587\u4F20\u8F93\u3002</p><h2 id="\u7279\u70B9-1" tabindex="-1">\u7279\u70B9 <a class="header-anchor" href="#\u7279\u70B9-1" aria-hidden="true">#</a></h2><ul><li><p>\u4FE1\u606F\u52A0\u5BC6\uFF1A\u6D4F\u89C8\u5668\u548C\u670D\u52A1\u5668\u4E4B\u95F4\u4EA4\u4E92\u7684\u4FE1\u606F\u65E0\u6CD5\u88AB\u7A83\u53D6\u3002</p></li><li><p>\u6821\u9A8C\u673A\u5236\uFF1A\u65E0\u6CD5\u7BE1\u6539\u901A\u4FE1\u7684\u5185\u5BB9\uFF0C\u56E0\u4E3A\u4E00\u65E6\u88AB\u7BE1\u6539\u5C31\u4E0D\u80FD\u6B63\u5E38\u663E\u793A\u3002</p></li><li><p>\u8EAB\u4EFD\u8BC1\u4E66\uFF1A\u63D0\u4F9B\u5F53\u524D\u62A5\u6587\u5B8C\u6574\u6027\u7684\u8BC1\u660E\u3002</p></li></ul><h2 id="\u4F18\u70B9" tabindex="-1">\u4F18\u70B9 <a class="header-anchor" href="#\u4F18\u70B9" aria-hidden="true">#</a></h2><ul><li><p>\u5728\u6570\u636E\u4F20\u8F93\u8FC7\u7A0B\u4E2D\uFF0C\u4F7F\u7528\u5BC6\u94A5\u52A0\u5BC6\uFF0C\u5B89\u5168\u6027\u66F4\u9AD8\u3002</p></li><li><p>\u80FD\u591F\u8BA4\u8BC1\u7528\u6237\u548C\u670D\u52A1\u5668\uFF0C\u786E\u4FDD\u6570\u636E\u53D1\u9001\u5230\u6B63\u786E\u7684\u7528\u6237\u548C\u670D\u52A1\u5668\u3002</p></li></ul><h2 id="\u7F3A\u70B9-1" tabindex="-1">\u7F3A\u70B9 <a class="header-anchor" href="#\u7F3A\u70B9-1" aria-hidden="true">#</a></h2><ul><li><p>\u5728 TCP \u4E09\u6B21\u63E1\u624B\u540E\uFF0C\u8FD8\u9700\u8981\u8FDB\u884C SSL/TLS \u63E1\u624B\uFF0C\u5BFC\u81F4\u63E1\u624B\u9636\u6BB5\u5EF6\u8FDF\u63D0\u9AD8\u3002</p></li><li><p>\u9700\u8981\u8FDB\u884C\u52A0\u89E3\u5BC6\u8BA1\u7B97\uFF0C\u5360\u7528 CPU \u8D44\u6E90\uFF0C\u4ECE\u800C\u5BFC\u81F4\u8BA1\u7B97\u6210\u672C\u63D0\u9AD8\u3002</p></li><li><p>\u9700\u8981\u8D2D\u4E70 SSL \u8BC1\u4E66\uFF0C\u529F\u80FD\u8D8A\u5F3A\u5927\uFF0C\u8BC1\u4E66\u4EF7\u683C\u8D8A\u8D35\uFF0C\u5BFC\u81F4\u90E8\u7F72\u6210\u672C\u5347\u9AD8\u3002</p></li></ul><h1 id="http-\u548C-https-\u533A\u522B" tabindex="-1">HTTP \u548C HTTPS \u533A\u522B <a class="header-anchor" href="#http-\u548C-https-\u533A\u522B" aria-hidden="true">#</a></h1><ol><li><p>HTTPS \u662F HTTP \u534F\u8BAE\u7684\u5B89\u5168\u7248\u672C\uFF0CHTTP \u534F\u8BAE\u7684\u6570\u636E\u4F20\u8F93\u662F\u660E\u6587\u7684\uFF0C\u662F\u4E0D\u5B89\u5168\u7684\uFF0CHTTPS \u4F7F\u7528\u4E86 SSL/TLS \u534F\u8BAE\u8FDB\u884C\u4E86\u52A0\u5BC6\u5904\u7406\uFF0C\u76F8\u5BF9\u66F4\u5B89\u5168\u3002</p></li><li><p>HTTP \u548C HTTPS \u4F7F\u7528\u8FDE\u63A5\u65B9\u5F0F\u4E0D\u540C\uFF0C\u9ED8\u8BA4\u7AEF\u53E3\u4E5F\u4E0D\u4E00\u6837\uFF0CHTTP\u662F 80\uFF0CHTTPS \u662F443\u3002</p></li><li><p>HTTPS \u7531\u4E8E\u9700\u8981\u8BBE\u8BA1\u52A0\u5BC6\u4EE5\u53CA\u591A\u6B21\u63E1\u624B\uFF0C\u6027\u80FD\u65B9\u9762\u4E0D\u5982 HTTP</p></li></ol>',18),h=[d];function T(r,p,c,o,s,n){return a(),t("div",null,h)}var u=e(l,[["render",T]]);export{S as __pageData,u as default};
