import{_ as e,o as a,c as i,a as l}from"./app.a2d27a6b.js";const _=JSON.parse('{"title":"\u6D4F\u89C8\u5668\u9875\u9762\u7684\u4FDD\u62A4\u673A\u5236--\u540C\u6E90\u7B56\u7565","description":"","frontmatter":{},"headers":[{"level":2,"title":"XSS\uFF08\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF09","slug":"xss\uFF08\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF09"},{"level":3,"title":"\u5B58\u50A8\u578B","slug":"\u5B58\u50A8\u578B"},{"level":3,"title":"\u53CD\u5C04\u578B","slug":"\u53CD\u5C04\u578B"},{"level":3,"title":"\u57FA\u4E8E DOM \u578B","slug":"\u57FA\u4E8E-dom-\u578B"},{"level":2,"title":"\u89E3\u51B3\u65B9\u6CD5","slug":"\u89E3\u51B3\u65B9\u6CD5"},{"level":2,"title":"\u53D1\u8D77 CSRF \u653B\u51FB\u6709\u4E09\u4E2A\u5FC5\u8981\u6761\u4EF6","slug":"\u53D1\u8D77-csrf-\u653B\u51FB\u6709\u4E09\u4E2A\u5FC5\u8981\u6761\u4EF6"},{"level":2,"title":"\u89E3\u51B3\u65B9\u6CD5","slug":"\u89E3\u51B3\u65B9\u6CD5-1"}],"relativePath":"studyCn/Network/XSSAndSRF.md"}'),t={name:"studyCn/Network/XSSAndSRF.md"},r=l('<h1 id="\u6D4F\u89C8\u5668\u9875\u9762\u7684\u4FDD\u62A4\u673A\u5236-\u540C\u6E90\u7B56\u7565" tabindex="-1">\u6D4F\u89C8\u5668\u9875\u9762\u7684\u4FDD\u62A4\u673A\u5236--\u540C\u6E90\u7B56\u7565 <a class="header-anchor" href="#\u6D4F\u89C8\u5668\u9875\u9762\u7684\u4FDD\u62A4\u673A\u5236-\u540C\u6E90\u7B56\u7565" aria-hidden="true">#</a></h1><p>\u540C\u6E90\u7B56\u7565\u6307\u7684\u662F\u6211\u4EEC\u8BBF\u95EE\u7AD9\u70B9\u7684\uFF1A<code>\u534F\u8BAE</code>\u3001<code>\u57DF\u540D</code>\u3001`\u7AEF\u53E3\u53F7 \u4E00\u81F4\u624D\u53EB\u540C\u6E90\uFF0C\u6709\u4E00\u4E2A\u4E0D\u4E00\u6837\uFF0C\u90FD\u4F1A\u88AB\u8BA4\u4E3A\u662F\u8DE8\u6E90\uFF08\u8DE8\u57DF\uFF09\u3002</p><p>\u6D4F\u89C8\u5668\u9ED8\u8BA4\u540C\u6E90\u7AD9\u70B9\u4E4B\u95F4\u662F\u53EF\u4EE5\u4E92\u76F8\u8BBF\u95EE\u8D44\u6E90\u548C\u64CD\u4F5C DOM \u7684\uFF0C\u800C\u4E0D\u540C\u5143\u6E90\u4E4B\u95F4\u60F3\u8981\u4E92\u76F8\u8BBF\u95EE\u8D44\u6E90\u6216\u8005\u64CD\u4F5C DOM \u7684\u8BDD\uFF0C\u5C31\u9700\u8981\u6DFB\u52A0\u4E00\u4E9B\u5B89\u5168\u7B56\u7565\u7684\u9650\u5236\u3002\u5982\u4E0B\uFF1A</p><ol><li><p>DOM \u5C42\u9762\uFF1A\u4E0D\u540C\u6E90\u7AD9\u70B9\u4E4B\u95F4\u4E0D\u80FD\u4E92\u76F8\u8BBF\u95EE\u548C\u64CD\u4F5C DOM\u3002</p></li><li><p>\u6570\u636E\u5C42\u9762\uFF1A\u4E0D\u80FD\u83B7\u53D6\u4E0D\u540C\u6E90\u7AD9\u70B9\u7684 Cookie\u3001LocalStorage\u3001indexDB \u7B49\u6570\u636E\u3002</p></li><li><p>\u7F51\u7EDC\u5C42\u9762\uFF1A\u4E0D\u80FD\u901A\u8FC7 XMLHttpRequest \u5411\u4E0D\u540C\u6E90\u7AD9\u70B9\u53D1\u9001\u8BF7\u6C42\u3002\uFF08\u540E\u6765\u57FA\u4E8E CORS \u53EF\u4EE5\u5B9E\u73B0\u4F46\u662F\u4E5F\u4F1A\u5B58\u5728\u4E00\u5B9A\u7684\u9650\u5236\uFF09</p></li></ol><p>\u5F53\u7136\uFF0C\u540C\u6E90\u7B56\u7565\u4E5F\u4E0D\u662F\u7EDD\u5BF9\u9694\u79BB\u4E0D\u540C\u6E90\u7684\u7AD9\u70B9\uFF0C\u6BD4\u5982 link\u3001img\u3001script \u6807\u7B7E\u90FD\u6CA1\u6709\u8DE8\u57DF\u9650\u5236\uFF0C\u8FD9\u4E5F\u5BFC\u81F4\u4E86\u4E00\u4E9B\u5B89\u5168\u95EE\u9898\u3002\u5982 XSS \u653B\u51FB\u548C CSRF \u653B\u51FB</p><h2 id="xss\uFF08\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF09" tabindex="-1">XSS\uFF08\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF09 <a class="header-anchor" href="#xss\uFF08\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF09" aria-hidden="true">#</a></h2><p>XSS \u53EB\u8DE8\u7AD9\u811A\u672C\u653B\u51FB\uFF0C\u539F\u672C\u5E94\u8BE5\u53EB CSS\uFF0C\u4F46\u662F\u4E3A\u4E86\u548C\u5C42\u53E0\u6837\u5F0F\u533A\u522B\u5F00\uFF0C\u53EB XSS\u3002XSS \u653B\u51FB\u662F\u4E00\u79CD\u4EE3\u7801\u6CE8\u5165\u653B\u51FB\uFF0C\u901A\u8FC7\u6076\u610F\u6CE8\u5165 JS \u811A\u672C\u5728\u6D4F\u89C8\u5668\u8FD0\u884C\uFF0C\u7136\u540E\u8C03\u53D6\u7528\u6237\u4FE1\u606F\u3002</p><p>\u9020\u6210 XSS \u653B\u51FB\u672C\u8D28\u4E0A\u8FD8\u662F\u56E0\u4E3A\u7F51\u7AD9\u6CA1\u6709\u8FC7\u6EE4\u6076\u610F\u4EE3\u7801\u3002\u5F53\u6076\u610F\u4EE3\u7801\u6DF7\u5165\u6B63\u5E38\u4EE3\u7801\u4E2D\u4E00\u8D77\u6267\u884C\u65F6\uFF0C\u6D4F\u89C8\u5668\u6CA1\u6709\u529E\u6CD5\u5206\u8FA8\u54EA\u4E9B\u662F\u53EF\u4FE1\u7684\uFF0C\u7136\u540E\u5BFC\u81F4\u6076\u610F\u4EE3\u7801\u4E5F\u88AB\u6267\u884C\u3002\u5F15\u8D77\u7684\u5371\u5BB3\u6709\uFF1A</p><ul><li><p>\u9875\u9762\u6570\u636E\u6216\u7528\u6237\u4FE1\u606F\u88AB\u7A83\u53D6\u3002\u5982 Cookie\u3001LocalStorage\u3001DOM \u7B49\u3002</p></li><li><p>\u4FEE\u6539 DOM\u3002\u6BD4\u5982\u4F2A\u9020\u767B\u9646\u7A97\u53E3\u6216\u5728\u9875\u9762\u751F\u6210\u6D6E\u7A97\u5E7F\u544A\u3002</p></li><li><p>\u76D1\u542C\u7528\u6237\u884C\u4E3A\u3002\u5982\u6DFB\u52A0 addEventListener \u6765\u76D1\u542C\u952E\u76D8\u4E8B\u4EF6\uFF0C\u83B7\u53D6\u7528\u6237\u5BC6\u7801\u3002</p></li><li><p>\u6D41\u91CF\u88AB\u52AB\u6301\u5411\u5176\u4ED6\u7F51\u7AD9\u3002\u7528\u6237\u521A\u8FDB\u5165\u4E00\u4E2A\u7F51\u7AD9\uFF0C\u5C31\u76F4\u63A5\u8DF3\u8F6C\u5230\u53E6\u5916\u4E00\u4E2A\u7F51\u7AD9\u4E0A\u3002</p></li></ul><p>XSS \u653B\u51FB\u6709\u4E09\u79CD\u7C7B\u578B\uFF1A\u5B58\u50A8\u578B\u3001\u53CD\u5C04\u578B\u3001DOM \u578B\u3002</p><h3 id="\u5B58\u50A8\u578B" tabindex="-1">\u5B58\u50A8\u578B <a class="header-anchor" href="#\u5B58\u50A8\u578B" aria-hidden="true">#</a></h3><p>\u5B58\u50A8\u578B XSS \u653B\u51FB\u4E3B\u8981\u662F\u5C06\u6076\u610F\u811A\u672C\u5B58\u50A8\u5230\u670D\u52A1\u7AEF\uFF0C\u5F53\u8BFB\u53D6\u5230\u8BE5\u6076\u610F\u811A\u672C\u65F6\uFF0C\u6D4F\u89C8\u5668\u5C31\u4F1A\u8BC6\u522B\u4E3A\u4E00\u6BB5 JS \u4EE3\u7801\u6765\u6267\u884C\u3002\u6BD4\u5982\u5728\u8BC4\u8BBA\u533A\uFF0C\u6709\u4EBA\u5199\u4E86\u4E00\u6BB5\u6076\u610F\u811A\u672C\u5E76\u63D0\u4EA4\uFF0C\u6076\u610F\u811A\u672C\u5C31\u4F1A\u88AB\u670D\u52A1\u5668\u5B58\u50A8\u5230\u6570\u636E\u5E93\u3002\u5F53\u522B\u4EBA\u8BBF\u95EE\u65F6\uFF0C\u52A0\u8F7D\u8FD9\u6BB5\u8BC4\u8BBA\uFF0C\u6D4F\u89C8\u5668\u5C31\u628A\u5B83\u8BC6\u522B\u4E3A JS \u4EE3\u7801\u6765\u6267\u884C\u3002</p><h3 id="\u53CD\u5C04\u578B" tabindex="-1">\u53CD\u5C04\u578B <a class="header-anchor" href="#\u53CD\u5C04\u578B" aria-hidden="true">#</a></h3><p>\u901A\u8FC7 URL \u53C2\u6570\u6CE8\u5165\u6076\u610F\u811A\u672C\uFF0C\u7ECF\u670D\u52A1\u5668\u89E3\u6790\u5E76\u54CD\u5E94\u540E\uFF0C\u51ED\u501F\u5728 HTML \u4E2D\u4F20\u56DE\u6D4F\u89C8\u5668\uFF0C\u7136\u540E\u6D4F\u89C8\u5668\u89E3\u6790\u65F6\u5C31\u4F1A\u6267\u884C\u6076\u610F\u811A\u672C\u3002\u6BD4\u5982\u6253\u5F00\u5305\u542B\u6076\u610F\u811A\u672C\u7684\u94FE\u63A5\uFF0C\u6253\u5F00\u540E\u4F1A\u5411\u670D\u52A1\u5668\u53D1\u9001\u8BF7\u6C42\uFF0C\u670D\u52A1\u5668\u4F1A\u83B7\u53D6 URL \u4E2D\u7684\u6570\u636E\u7136\u540E\u51ED\u501F\u5728 HTML \u4E0A\u8FD4\u56DE\uFF0C\u7136\u540E\u6267\u884C\u3002\u5B83\u548C\u5B58\u50A8\u578B\u7684\u533A\u522B\u5728\u4E8E\u4E0D\u4F1A\u5B58\u50A8\u5728\u670D\u52A1\u5668\u4E2D\u3002</p><h3 id="\u57FA\u4E8E-dom-\u578B" tabindex="-1">\u57FA\u4E8E DOM \u578B <a class="header-anchor" href="#\u57FA\u4E8E-dom-\u578B" aria-hidden="true">#</a></h3><p>\u901A\u8FC7\u4E00\u5B9A\u7684\u624B\u6BB5\u5728\u7F51\u9875\u5411\u670D\u52A1\u7AEF\u8BF7\u6C42\u8D44\u6E90\u65F6\uFF0C\u52AB\u6301\u5E76\u4FEE\u6539\u9875\u9762\u7684\u6570\u636E\uFF0C\u63D2\u5165\u6076\u610F\u4EE3\u7801\u3002</p><h2 id="\u89E3\u51B3\u65B9\u6CD5" tabindex="-1">\u89E3\u51B3\u65B9\u6CD5 <a class="header-anchor" href="#\u89E3\u51B3\u65B9\u6CD5" aria-hidden="true">#</a></h2><ul><li>\u5728\u670D\u52A1\u7AEF\u5BF9 script \u6807\u7B7E\u8FDB\u884C\u8F6C\u4E49\u6216\u8FC7\u6EE4\uFF0C\u518D\u4F20\u56DE\u7ED9\u6D4F\u89C8\u5668\uFF0C\u8FD9\u6837\u6D4F\u89C8\u5668\u5728 HTML \u89E3\u6790\u65F6\u5C31\u4E0D\u4F1A\u628A script \u6807\u7B7E\u5F53\u4F5C JS \u4EE3\u7801\u6267\u884C\u4E86\u3002</li></ul><p>\u5229\u7528 http-only\u3002\u5F53 Cookie \u8BBE\u7F6E http-only \u540E\uFF0C\u4F1A\u7981\u6B62 JavaScript \u6765\u8BBF\u95EE Cookie\u3002</p><ul><li>\u5145\u5206\u5229\u7528 CSP\u3002\u5185\u5BB9\u5B89\u5168\u7B56\u7565\uFF08CSP\uFF09\u662F\u4E00\u4E2A\u989D\u5916\u7684\u5B89\u5168\u5C42\uFF0C\u4F1A\u9650\u5236\u52A0\u8F7D\u5176\u4ED6\u57DF\u4E0B\u7684\u8D44\u6E90\u6587\u4EF6\u3001\u7981\u6B62\u5411\u7B2C\u4E09\u65B9\u63D0\u4EA4\u6570\u636E\u3002</li></ul><h1 id="csrf\uFF08\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\uFF09" tabindex="-1">CSRF\uFF08\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\uFF09 <a class="header-anchor" href="#csrf\uFF08\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\uFF09" aria-hidden="true">#</a></h1><p>CSRF \u662F\u8DE8\u7AD9\u8BF7\u6C42\u4F2A\u9020\u653B\u51FB\uFF0C\u987E\u540D\u601D\u4E49\uFF0C\u5C31\u662F\u7B2C\u4E09\u65B9\u5229\u7528\u7528\u6237\u7684\u767B\u5F55\u4FE1\u606F\u4F2A\u9020\u6210\u7528\u6237\u53D1\u8D77\u8DE8\u57DF\u8BF7\u6C42\u3002\u6BD4\u5982\u90AE\u7BB1\u91CC\u7684\u4E71\u4E03\u516B\u7CDF\u94FE\u63A5\uFF0C\u6253\u5F00\u94FE\u63A5\u7684\u65F6\u5019\u90AE\u7BB1\u5904\u4E8E\u767B\u9646\u72B6\u6001\uFF0C\u7B2C\u4E09\u65B9\u5C31\u53EF\u4EE5\u5229\u7528\u8FD9\u4E2A\u767B\u9646\u72B6\u6001\uFF0C\u4F2A\u9020\u5E26\u6709\u6B63\u786E Cookie \u7684 http \u8BF7\u6C42\uFF0C\u7ED5\u8FC7\u540E\u53F0\u9A8C\u8BC1\uFF0C\u5192\u5145\u7528\u6237\u8FDB\u884C\u4E00\u4E9B\u64CD\u4F5C</p><h2 id="\u53D1\u8D77-csrf-\u653B\u51FB\u6709\u4E09\u4E2A\u5FC5\u8981\u6761\u4EF6" tabindex="-1">\u53D1\u8D77 CSRF \u653B\u51FB\u6709\u4E09\u4E2A\u5FC5\u8981\u6761\u4EF6 <a class="header-anchor" href="#\u53D1\u8D77-csrf-\u653B\u51FB\u6709\u4E09\u4E2A\u5FC5\u8981\u6761\u4EF6" aria-hidden="true">#</a></h2><ul><li><p>\u76EE\u6807\u7F51\u7AD9\u6709 CSRF \u6F0F\u6D1E</p></li><li><p>\u7528\u6237\u767B\u5F55\u8FC7\u76EE\u6807\u7F51\u7AD9\uFF0C\u5E76\u4E14\u6D4F\u89C8\u5668\u4FDD\u5B58\u4E86\u767B\u9646\u72B6\u6001\u3002</p></li><li><p>\u7528\u6237\u4E3B\u52A8\u6253\u5F00\u7B2C\u4E09\u65B9\u7AD9\u70B9\u3002</p></li></ul><p>CSRF \u672C\u8D28\u4E0A\u662F\u5229\u7528\u8FDB\u884C HTTP \u540C\u6E90\u8BF7\u6C42\u65F6\u4F1A\u643A\u5E26 Cookie \u4FE1\u606F\u8FD9\u4E00\u7279\u70B9\uFF0C\u5B9E\u73B0\u5192\u5145\u7528\u6237\u3002</p><h2 id="\u89E3\u51B3\u65B9\u6CD5-1" tabindex="-1">\u89E3\u51B3\u65B9\u6CD5 <a class="header-anchor" href="#\u89E3\u51B3\u65B9\u6CD5-1" aria-hidden="true">#</a></h2><ul><li><p>Cookie Hashing\uFF08\u670D\u52A1\u5668\u751F\u6210 Cookie \u4F20\u9012\u7ED9\u5BA2\u6237\u7AEF\u65F6\u4F1A\u751F\u6210\u4E00\u4E2A\u968F\u673A\u6570\u5B58\u5728\u5176\u4E2D\uFF09\u3002\u6700\u7B80\u5355\u6709\u6548\u65B9\u5F0F\uFF0C\u56E0\u4E3A\u653B\u51FB\u8005\u7406\u8BBA\u4E0A\u65E0\u6CD5\u83B7\u53D6\u7B2C\u4E09\u65B9\u7684Cookie\uFF0C\u6240\u4EE5\u4F2A\u9020 Cookie \u5931\u8D25\uFF0C\u65E0\u6CD5\u901A\u8FC7\u7528\u6237\u9A8C\u8BC1\u3002</p></li><li><p>\u643A\u5E26 token\u3002\u5BA2\u6237\u7AEF\u5411\u670D\u52A1\u5668\u8BF7\u6C42 token\uFF08\u4EE4\u724C\uFF09\uFF0C\u670D\u52A1\u5668\u8FD4\u56DE token \u7ED9\u5BA2\u6237\u7AEF\uFF0C\u5E76\u4E14\u5BA2\u6237\u7AEF\u5728\u4E4B\u540E\u7684\u6240\u6709\u8BF7\u6C42\u4E2D\u90FD\u8981\u5E26\u4E0A token \u4EE5\u4F5C\u8EAB\u4EFD\u9A8C\u8BC1\u3002</p></li><li><p>Origin \u548C Referer\u3002\u670D\u52A1\u5668\u9A8C\u8BC1 Referer \u662F\u5426\u4ECE\u7B2C\u4E09\u65B9\u7F51\u7AD9\u53D1\u51FA\u6765\u7684\uFF0C\u963B\u6B62\u7B2C\u4E09\u65B9\u7F51\u7AD9\u8BF7\u6C42\u63A5\u53E3\u3002\u4F46\u662F\u8FD9\u4E24\u8005\u53EF\u4EE5\u901A\u8FC7 ajax \u81EA\u5B9A\u4E49\u8BF7\u6C42\u5934\u7684\u65B9\u5F0F\u88AB\u4F2A\u9020\u3002</p></li></ul>',27),d=[r];function o(p,s,h,n,c,S){return a(),i("div",null,d)}var C=e(t,[["render",o]]);export{_ as __pageData,C as default};
