import{_ as s,o as e,c as a,a as o}from"./app.d8277619.js";const u=JSON.parse('{"title":"\u8D44\u6E90\u7BA1\u7406(asset) - VitePress\u4E2D\u6587\u6587\u6863","description":"VitePress\u4E2D\u6587\u6587\u6863\u4E4B\u8D44\u6E90\u7BA1\u7406","frontmatter":{"title":"\u8D44\u6E90\u7BA1\u7406(asset) - VitePress\u4E2D\u6587\u6587\u6863","description":"VitePress\u4E2D\u6587\u6587\u6863\u4E4B\u8D44\u6E90\u7BA1\u7406","tags":["VitePress\u4E2D\u6587\u6587\u6863,VitePress\u8D44\u6E90\u7BA1\u7406"],"head":[["meta",{"name":"keywords","content":"VitePress\u4E2D\u6587\u6587\u6863,VitePress\u8D44\u6E90\u7BA1\u7406"}]]},"headers":[{"level":2,"title":"Public \u6587\u4EF6","slug":"public-\u6587\u4EF6"},{"level":2,"title":"Base URL","slug":"base-url"}],"relativePath":"vitepressCn/asset-handling.md"}'),n={name:"vitepressCn/asset-handling.md"},l=o(`<h1 id="\u8D44\u6E90\u7BA1\u7406" tabindex="-1">\u8D44\u6E90\u7BA1\u7406 <a class="header-anchor" href="#\u8D44\u6E90\u7BA1\u7406" aria-hidden="true">#</a></h1><details class="details custom-block"><summary>\u539F\u6587</summary><p>All Markdown files are compiled into Vue components and processed by Vite. You can, and should, reference any assets using relative URLs:</p><div class="language-md"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">![</span><span style="color:#C3E88D;">An image</span><span style="color:#89DDFF;">](</span><span style="color:#A6ACCD;text-decoration:underline;">./image.png</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre></div><p>You can reference static assets in your markdown files, your *.vue components in the theme, styles and plain .css files either using absolute public paths (based on project root) or relative paths (based on your file system). The latter is similar to the behavior you are used to if you have used vue-cli or webpack&#39;s file-loader.</p><p>Common image, media, and font filetypes are detected and included as assets automatically.</p><p>All referenced assets, including those using absolute paths, will be copied to the dist folder with a hashed file name in the production build. Never-referenced assets will not be copied. Similar to vue-cli, image assets smaller than 4kb will be base64 inlined.</p><p>All static path references, including absolute paths, should be based on your working directory structure.</p></details><p>\u6240\u6709\u7684 Markdown \u6587\u4EF6\u90FD\u88AB\u7F16\u8BD1\u6210 Vue \u7EC4\u4EF6\u5E76\u7531 Vite \u5904\u7406\u3002\u4F60\u53EF\u4EE5\u4E14\u5E94\u8BE5\u4F7F\u7528\u76F8\u5BF9\u7684 URL \u6765\u5F15\u7528\u4EFB\u4F55\u8D44\u6E90\u3002</p><div class="language-md"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">![</span><span style="color:#C3E88D;">An image</span><span style="color:#89DDFF;">](</span><span style="color:#A6ACCD;text-decoration:underline;">./image.png</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre></div><p>\u4F60\u53EF\u4EE5\u4F7F\u7528\u7EDD\u5BF9\u516C\u5171\u8DEF\u5F84\uFF08\u57FA\u4E8E\u9879\u76EE\u6839\uFF09\u6216\u76F8\u5BF9\u8DEF\u5F84\uFF08\u57FA\u4E8E\u4F60\u7684\u6587\u4EF6\u7CFB\u7EDF\uFF09\u6765\u5F15\u7528\u4F60\u7684 Markdown \u6587\u4EF6\u4E2D\u7684\u9759\u6001\u9759\u6001\u8D44\u6E90\u3001\u4E3B\u9898\u4E2D\u7684<code>*.vue</code>\u7EC4\u4EF6\u3001\u6837\u5F0F\u548C\u666E\u901A<code>.css</code>\u6587\u4EF6\u3002\u540E\u8005\u7C7B\u4F3C\u4E8E\u4F60\u4F7F\u7528<code>vue-cli</code>\u6216<code>webpack</code>\u7684\u6587\u4EF6\u52A0\u8F7D\u5668\u6240\u4E60\u60EF\u7684\u64CD\u4F5C\u3002</p><p>\u5E38\u89C1\u7684\u56FE\u50CF\u3001\u5A92\u4F53\u548C\u5B57\u4F53\u6587\u4EF6\u7C7B\u578B\u4F1A\u88AB\u81EA\u52A8\u68C0\u6D4B\u5E76\u4F5C\u4E3A\u8D44\u6E90\u5305\u542B\u3002</p><p>\u6240\u6709\u88AB\u5F15\u7528\u7684\u8D44\u6E90\uFF0C\u5305\u62EC\u90A3\u4E9B\u4F7F\u7528\u7EDD\u5BF9\u8DEF\u5F84\u7684\u8D44\u6E90\uFF0C\u5728\u751F\u4EA7\u6784\u5EFA\u4E2D\u90FD\u4F1A\u88AB\u590D\u5236\u5230<code>dist</code>\u6587\u4EF6\u5939\u4E2D\uFF0C\u5E76\u5E26\u6709\u54C8\u5E0C\u7684\u6587\u4EF6\u540D\u3002\u4ECE\u672A\u5F15\u7528\u8FC7\u7684\u9759\u6001\u8D44\u6E90\u5C06\u4E0D\u4F1A\u88AB\u590D\u5236\u3002\u4E0E<code>vue-cli</code>\u7C7B\u4F3C\uFF0C\u5C0F\u4E8E<code>4kb</code>\u7684\u56FE\u7247\u9759\u6001\u8D44\u6E90\u5C06\u88AB\u8F6C\u6362\u6210<code>base64</code>\u3002</p><p>\u6240\u6709\u7684\u9759\u6001\u8DEF\u5F84\u5F15\u7528\uFF0C\u5305\u62EC\u7EDD\u5BF9\u8DEF\u5F84\uFF0C\u90FD\u5E94\u8BE5\u4EE5\u4F60\u7684\u5DE5\u4F5C\u76EE\u5F55\u7ED3\u6784\u4E3A\u57FA\u7840\u3002</p><h2 id="public-\u6587\u4EF6" tabindex="-1">Public \u6587\u4EF6 <a class="header-anchor" href="#public-\u6587\u4EF6" aria-hidden="true">#</a></h2><details class="details custom-block"><summary>\u539F\u6587</summary><p>Sometimes you may need to provide static assets that are not directly referenced in any of your Markdown or theme components (for example, favicons and PWA icons). The public directory under project root (docs folder if you&#39;re running vitepress build docs) can be used as an escape hatch to provide static assets that either are never referenced in source code (e.g. robots.txt), or must retain the exact same file name (without hashing).</p><p>Assets placed in public will be copied to the root of the dist directory as-is.</p><p>Note that you should reference files placed in public using root absolute path - for example, public/icon.png should always be referenced in source code as /icon.png.</p></details><p>\u6709\u65F6\u4F60\u53EF\u80FD\u9700\u8981\u63D0\u4F9B\u5728\u4F60\u7684 Markdown \u6216\u4E3B\u9898\u7EC4\u4EF6\u4E2D\u6CA1\u6709\u76F4\u63A5\u5F15\u7528\u7684\u9759\u6001\u8D44\u6E90\uFF08\u4F8B\u5982\uFF0C<code>favicons</code>\u548C<code>PWA</code>\u56FE\u6807\uFF09\u3002\u9879\u76EE\u6839\u76EE\u5F55\u4E0B\u7684<code>public</code>\u76EE\u5F55\uFF08\u5982\u679C\u4F60\u6B63\u5728\u8FD0\u884C<code>vitepress build docs</code>\u7684\u8BDD\uFF0C<code>docs</code>\u6587\u4EF6\u5939\uFF09\u53EF\u4EE5\u4F5C\u4E3A\u4E00\u4E2A\u7528\u6765\u63D0\u4F9B\u5728\u6E90\u4EE3\u7801\u4E2D\u4ECE\u672A\u88AB\u5F15\u7528\u7684\u9759\u6001\u8D44\u6E90\uFF08\u4F8B\u5982<code>robots.txt</code>\uFF09\uFF0C\u6216\u8005\u5FC5\u987B\u4FDD\u6301\u5B8C\u5168\u76F8\u540C\u7684\u6587\u4EF6\u540D\uFF08\u6CA1\u6709\u54C8\u5E0C\u503C\uFF09\u7684\u76EE\u5F55\u3002</p><p>\u653E\u5728<code>public</code>\u76EE\u5F55\u4E0B\u7684\u8D44\u6E90\u5C06\u88AB\u539F\u6837\u590D\u5236\u5230<code>dist</code>\u76EE\u5F55\u7684\u6839\u90E8\u3002</p><p>\u8BF7\u6CE8\u610F\uFF0C\u4F60\u5E94\u8BE5\u4F7F\u7528\u7EDD\u5BF9\u7684\u6839\u8DEF\u5F84\u6765\u5F15\u7528\u653E\u7F6E\u5728<code>public</code>\u4E2D\u7684\u6587\u4EF6\uFF0C\u5982<code>public/icon.png</code>\u5728\u6E90\u4EE3\u7801\u4E2D\u5E94\u8BE5\u603B\u662F\u88AB\u5F15\u7528\u4E3A<code>/icon.png</code>\u3002</p><h2 id="base-url" tabindex="-1">Base URL <a class="header-anchor" href="#base-url" aria-hidden="true">#</a></h2><details class="details custom-block"><summary>\u539F\u6587</summary><p>If your site is deployed to a non-root URL, you will need to set the base option in .vitepress/config.js. For example, if you plan to deploy your site to <a href="https://foo.github.io/bar/" target="_blank" rel="noopener noreferrer">https://foo.github.io/bar/</a>, then base should be set to &#39;/bar/&#39; (it should always start and end with a slash).</p><p>All your static asset paths are automatically processed to adjust for different base config values. For example, if you have an absolute reference to an asset under public in your markdown:</p><div class="language-md"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">![</span><span style="color:#C3E88D;">An image</span><span style="color:#89DDFF;">](</span><span style="color:#A6ACCD;text-decoration:underline;">/image-inside-public.png</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre></div><p>You do not need to update it when you change the base config value in this case.</p><p>However, if you are authoring a theme component that links to assets dynamically, e.g. an image whose src is based on a theme config value:</p><div class="language-vue"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&lt;img :src=&quot;theme.logoPath&quot; /&gt;</span></span>
<span class="line"></span></code></pre></div><p>In this case it is recommended to wrap the path with the withBase helper provided by VitePress:</p><div class="language-vue"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">script</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">setup</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#89DDFF;font-style:italic;">import</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">withBase</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">useData</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;font-style:italic;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">vitepress</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#A6ACCD;"> theme </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">useData</span><span style="color:#A6ACCD;">()</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">template</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span><span style="color:#89DDFF;"> :</span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#82AAFF;">withBase</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">theme</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">logoPath</span><span style="color:#89DDFF;">)</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;"> /&gt;</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">template</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre></div></details><p>\u5982\u679C\u4F60\u7684\u7F51\u7AD9\u88AB\u90E8\u7F72\u5230\u4E00\u4E2A\u975E\u6839\u7684<code>URL</code>\uFF0C\u4F60\u5C06\u9700\u8981\u5728<code>.vitepress/config.js</code>\u4E2D\u8BBE\u7F6E<code>base</code>\u9009\u9879\u3002\u4F8B\u5982\uFF0C\u5982\u679C\u6253\u7B97\u628A\u4F60\u7684\u7F51\u7AD9\u90E8\u7F72\u5230<code>https://foo.github.io/bar/</code>\uFF0C\u90A3\u4E48<code>base</code>\u5E94\u8BE5\u88AB\u8BBE\u7F6E\u4E3A<code>&#39;/bar/&#39;</code>\uFF08\u5B83\u5E94\u8BE5\u603B\u662F\u4EE5\u659C\u7EBF\u5F00\u59CB\u548C\u7ED3\u675F\uFF09\u3002</p><p>\u4F60\u6240\u6709\u7684\u9759\u6001\u8D44\u6E90\u8DEF\u5F84\u90FD\u4F1A\u88AB\u81EA\u52A8\u5904\u7406\uFF0C\u4EE5\u9002\u5E94\u4E0D\u540C\u7684\u57FA\u7840\u914D\u7F6E\u503C\u3002\u4F8B\u5982\uFF0C\u5982\u679C\u5728\u4F60\u7684<code>Markdown</code>\u4E2D\u5BF9<code>public</code>\u4E0B\u7684\u8D44\u6E90\u6709\u4E00\u4E2A\u7EDD\u5BF9\u5F15\u7528\u3002</p><div class="language-md"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">![</span><span style="color:#C3E88D;">An image</span><span style="color:#89DDFF;">](</span><span style="color:#A6ACCD;text-decoration:underline;">/image-inside-public.png</span><span style="color:#89DDFF;">)</span></span>
<span class="line"></span></code></pre></div><p>\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u5F53\u4F60\u6539\u53D8<code>base</code>\u914D\u7F6E\u503C\u65F6\uFF0C\u4F60\u4E0D\u9700\u8981\u66F4\u65B0\u5B83\u3002</p><p>\u7136\u800C\uFF0C\u5982\u679C\u4F60\u6B63\u5728\u7F16\u5199\u4E00\u4E2A\u52A8\u6001\u94FE\u63A5\u5230\u8D44\u6E90\u7684\u4E3B\u9898\u7EC4\u4EF6\uFF0C\u4F8B\u5982\uFF0C\u4E00\u4E2A\u56FE\u7247\u7684<code>src</code>\u662F\u57FA\u4E8E\u4E3B\u9898\u914D\u7F6E\u503C\u7684\u3002</p><div class="language-vue"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">&lt;img :src=&quot;theme.logoPath&quot; /&gt;</span></span>
<span class="line"></span></code></pre></div><p>\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u5EFA\u8BAE\u7528<code>VitePress</code>\u63D0\u4F9B\u7684<a href="/vitepressCn/api.html#withBase"><code>withBase</code> helper</a>\u6765\u5305\u88C5\u8DEF\u5F84\u3002</p><div class="language-vue"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">script</span><span style="color:#A6ACCD;"> </span><span style="color:#C792EA;">setup</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#89DDFF;font-style:italic;">import</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">withBase</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">useData</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;font-style:italic;">from</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">vitepress</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#A6ACCD;"> theme </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">useData</span><span style="color:#A6ACCD;">()</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">script</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">template</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">&lt;</span><span style="color:#F07178;">img</span><span style="color:#89DDFF;"> :</span><span style="color:#C792EA;">src</span><span style="color:#89DDFF;">=</span><span style="color:#89DDFF;">&quot;</span><span style="color:#82AAFF;">withBase</span><span style="color:#89DDFF;">(</span><span style="color:#A6ACCD;">theme</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">logoPath</span><span style="color:#89DDFF;">)</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;"> /&gt;</span></span>
<span class="line"><span style="color:#89DDFF;">&lt;/</span><span style="color:#F07178;">template</span><span style="color:#89DDFF;">&gt;</span></span>
<span class="line"></span></code></pre></div>`,23),p=[l];function t(c,r,i,d,y,D){return e(),a("div",null,p)}var h=s(n,[["render",t]]);export{u as __pageData,h as default};
