import{_ as s,o as a,c as e,a as n}from"./app.d8277619.js";const u=JSON.parse('{"title":"VitePress\u5165\u95E8 - VitePress\u4E2D\u6587\u6587\u6863","description":"VitePress\u4E2D\u6587\u6587\u6863\u4E4BVitePress\u5165\u95E8","frontmatter":{"title":"VitePress\u5165\u95E8 - VitePress\u4E2D\u6587\u6587\u6863","description":"VitePress\u4E2D\u6587\u6587\u6863\u4E4BVitePress\u5165\u95E8","tags":["VitePress\u4E2D\u6587\u6587\u6863,VitePress\u5165\u95E8"],"head":[["meta",{"name":"keywords","content":"VitePress\u4E2D\u6587\u6587\u6863,VitePress\u5165\u95E8"}]]},"headers":[{"level":2,"title":"Step. 1: \u521B\u5EFA\u65B0\u9879\u76EE","slug":"step-1-\u521B\u5EFA\u65B0\u9879\u76EE"},{"level":2,"title":"Step. 2: \u5B89\u88C5 VitePress","slug":"step-2-\u5B89\u88C5-vitepress"},{"level":2,"title":"Step. 3: \u5F00\u542F\u5F00\u53D1\u73AF\u5883","slug":"step-3-\u5F00\u542F\u5F00\u53D1\u73AF\u5883"},{"level":2,"title":"Step. 4: \u6DFB\u52A0\u66F4\u591A\u9875\u9762","slug":"step-4-\u6DFB\u52A0\u66F4\u591A\u9875\u9762"},{"level":2,"title":"\u4E0B\u4E00\u6B65\u505A\u4EC0\u4E48\uFF1F","slug":"\u4E0B\u4E00\u6B65\u505A\u4EC0\u4E48\uFF1F"}],"relativePath":"vitepressCn/getting-started.md"}'),p={name:"vitepressCn/getting-started.md"},o=n(`<h1 id="\u5165\u95E8" tabindex="-1">\u5165\u95E8 <a class="header-anchor" href="#\u5165\u95E8" aria-hidden="true">#</a></h1><details class="details custom-block"><summary>\u539F\u6587</summary><p>This section will help you build a basic VitePress documentation site from ground up. If you already have an existing project and would like to keep documentation inside the project, start from Step 2.</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>VitePress is currently in alpha status. It is already suitable for out-of-the-box documentation use, but the config and theming API may still change between minor releases.</p></div></details><p>\u672C\u8282\u5C06\u5E2E\u52A9\u4F60\u4ECE\u5934\u5F00\u59CB\u5EFA\u7ACB\u4E00\u4E2A\u57FA\u672C\u7684VitePress\u6587\u6863\u7F51\u7AD9\u3002\u5982\u679C\u4F60\u5DF2\u7ECF\u6709\u4E00\u4E2A\u73B0\u6709\u7684\u9879\u76EE\uFF0C\u5E76\u5E0C\u671B\u5728\u9879\u76EE\u4E2D\u4FDD\u7559\u6587\u6863\uFF0C\u8BF7\u4ECE\u7B2C2\u6B65\u5F00\u59CB\u3002</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>VitePress \u76EE\u524D\u5904\u4E8E alpha \u72B6\u6001\u3002\u5B83\u5DF2\u7ECF\u9002\u5408\u4E8E\u5F00\u7BB1\u5373\u7528\u7684\u6587\u6863\u7AD9\u70B9\u4F7F\u7528\uFF0C\u4F46\u914D\u7F6E\u548C\u4E3B\u9898\u5316\u7684 API \u4ECD\u53EF\u80FD\u5728\u5C0F\u7248\u672C\u4E4B\u95F4\u53D1\u751F\u53D8\u5316\u3002</p></div><h2 id="step-1-\u521B\u5EFA\u65B0\u9879\u76EE" tabindex="-1">Step. 1: \u521B\u5EFA\u65B0\u9879\u76EE <a class="header-anchor" href="#step-1-\u521B\u5EFA\u65B0\u9879\u76EE" aria-hidden="true">#</a></h2><p>\u521B\u5EFA\u5E76\u8FDB\u5165\u65B0\u76EE\u5F55</p><div class="language-sh"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">$ mkdir vitepress-starter </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">cd</span><span style="color:#A6ACCD;"> vitepress-starter</span></span>
<span class="line"></span></code></pre></div><p>\u7136\u540E\u521D\u59CB\u5316\u5305\u7BA1\u7406\u5668</p><div class="language-sh"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">$ yarn init</span></span>
<span class="line"></span></code></pre></div><h2 id="step-2-\u5B89\u88C5-vitepress" tabindex="-1">Step. 2: \u5B89\u88C5 VitePress <a class="header-anchor" href="#step-2-\u5B89\u88C5-vitepress" aria-hidden="true">#</a></h2><p>\u5B89\u88C5 VitePress \u548C Vue \u4F5C\u4E3A\u9879\u76EE\u7684\u5F00\u53D1\u4F9D\u8D56</p><div class="language-sh"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">$ yarn add --dev vitepress vue</span></span>
<span class="line"></span></code></pre></div><details class="details custom-block"><summary>\u76F8\u5173\u4F9D\u8D56\u7F3A\u5931\u8B66\u544A\uFF1F</summary><p>@docsearch/js \u5982\u679C\u770B\u5230\u76F8\u5173\u4F9D\u8D56\u7F3A\u5931\u5F15\u8D77\u7684\u5931\u8D25\uFF0C\u4F60\u53EF\u4EE5\u5C1D\u8BD5\u4F7F\u7528\u4EE5\u4E0B\u89E3\u51B3\u65B9\u6848:</p><p>On Yarn v2/v3, \u5C06\u5176\u6DFB\u52A0\u5230\u4F60\u7684 rc \u6587\u4EF6\u4E2D (.yarnrc.yml by default):</p><div class="language-yaml"><span class="copy"></span><pre><code><span class="line"><span style="color:#F07178;">packageExtensions</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">@docsearch/react@*</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F07178;">peerDependenciesMeta</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">@types/react</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">        </span><span style="color:#F07178;">optional</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FF9CAC;">true</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">react</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">        </span><span style="color:#F07178;">optional</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FF9CAC;">true</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">react-dom</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">:</span></span>
<span class="line"><span style="color:#A6ACCD;">        </span><span style="color:#F07178;">optional</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FF9CAC;">true</span></span>
<span class="line"></span></code></pre></div><p>On PNPM, \u6DFB\u52A0\u5230\u4F60\u7684 package.json:</p><div class="language-json"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">pnpm</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">: </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">peerDependencyRules</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#FFCB6B;">ignoreMissing</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">[</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">@algolia/client-search</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">@types/react</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">react</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">      </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">react-dom</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">]</span></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div></details><p>\u521B\u5EFA\u7B2C\u4E00\u4E2A\u6587\u6863</p><div class="language-sh"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">$ mkdir docs </span><span style="color:#89DDFF;">&amp;&amp;</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">echo</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;"># Hello VitePress</span><span style="color:#89DDFF;">&#39;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&gt;</span><span style="color:#A6ACCD;"> docs/index.md</span></span>
<span class="line"></span></code></pre></div><h2 id="step-3-\u5F00\u542F\u5F00\u53D1\u73AF\u5883" tabindex="-1">Step. 3: \u5F00\u542F\u5F00\u53D1\u73AF\u5883 <a class="header-anchor" href="#step-3-\u5F00\u542F\u5F00\u53D1\u73AF\u5883" aria-hidden="true">#</a></h2><p>\u5728 package.json \u4E2D\u6DFB\u52A0\u811A\u672C\u547D\u4EE4</p><div class="language-js"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">...</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">scripts</span><span style="color:#89DDFF;">&quot;</span><span style="color:#F07178;">: </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#F07178;">docs:dev</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">vitepress dev docs</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#F07178;">docs:build</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">vitepress build docs</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#F07178;">docs:serve</span><span style="color:#89DDFF;">&quot;</span><span style="color:#89DDFF;">:</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">vitepress serve docs</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">},</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">...</span></span>
<span class="line"><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><p>\u5728\u672C\u5730\u5F00\u53D1\u73AF\u5883\u5F00\u542F\u670D\u52A1\u5668</p><div class="language-sh"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">$ yarn docs:dev</span></span>
<span class="line"></span></code></pre></div><p>VitePress \u5C06\u5728 <code>http://localhost:3000</code>\uFF0C\u542F\u52A8\u70ED\u91CD\u8F7D\u5F00\u53D1\u670D\u52A1\u5668\u3002</p><h2 id="step-4-\u6DFB\u52A0\u66F4\u591A\u9875\u9762" tabindex="-1">Step. 4: \u6DFB\u52A0\u66F4\u591A\u9875\u9762 <a class="header-anchor" href="#step-4-\u6DFB\u52A0\u66F4\u591A\u9875\u9762" aria-hidden="true">#</a></h2><details class="details custom-block"><summary>\u539F\u6587</summary><p>Let&#39;s add another page to the site. Create a file name <a href="http://getting-started.md" target="_blank" rel="noopener noreferrer">getting-started.md</a> along with <a href="http://index.md" target="_blank" rel="noopener noreferrer">index.md</a> you&#39;ve created in Step. 2. Now your directory structure should look like this.</p></details><p>\u8BA9\u6211\u4EEC\u5728\u7F51\u7AD9\u4E0A\u6DFB\u52A0\u53E6\u4E00\u4E2A\u9875\u9762\u3002\u4E0E\u4F60\u5728\u6B65\u9AA4 2 \u4E2D\u521B\u5EFA\u7684 <a href="http://index.md" target="_blank" rel="noopener noreferrer">index.md</a> \u4E00\u6837\u521B\u5EFA\u4E00\u4E2A\u540D\u4E3A <a href="http://getting-started.md" target="_blank" rel="noopener noreferrer">getting-started.md</a> \u7684\u6587\u4EF6\u3002\u73B0\u5728\u4F60\u7684\u76EE\u5F55\u7ED3\u6784\u5E94\u8BE5\u662F\u8FD9\u6837\u7684\u3002</p><div class="language-"><span class="copy"></span><pre><code><span class="line"><span style="color:#A6ACCD;">.</span></span>
<span class="line"><span style="color:#A6ACCD;">\u251C\u2500 docs</span></span>
<span class="line"><span style="color:#A6ACCD;">\u2502  \u251C\u2500 getting-started.md</span></span>
<span class="line"><span style="color:#A6ACCD;">\u2502  \u2514\u2500 index.md</span></span>
<span class="line"><span style="color:#A6ACCD;">\u2514\u2500 package.json</span></span>
<span class="line"><span style="color:#A6ACCD;"></span></span></code></pre></div><details class="details custom-block"><summary>\u539F\u6587</summary><p>Then, try to access <code>http://localhost:3000/getting-started</code> and you should see the content of getting-started is shown.</p><p>This is how VitePress works basically. The directory structure corresponds with the URL path. You add files, and just try to access it.</p></details><p>\u7136\u540E\uFF0C\u5C1D\u8BD5\u8BBF\u95EE <code>http://localhost:3000/getting-started</code> \uFF0C\u4F60\u5E94\u8BE5\u770B\u5230\u663E\u793A\u7684\u662F Getting-started \u7684\u5185\u5BB9\u3002</p><p>\u8FD9\u5C31\u662F VitePress \u7684\u57FA\u672C\u5DE5\u4F5C\u65B9\u5F0F\u3002\u76EE\u5F55\u7ED3\u6784\u4E0E URL \u8DEF\u5F84\u76F8\u5BF9\u5E94\u3002\u4F60\u6DFB\u52A0\u6587\u4EF6\u7136\u540E\u5C1D\u8BD5\u8BBF\u95EE\u5B83\u5C31\u53EF\u4EE5\u4E86\u3002</p><h2 id="\u4E0B\u4E00\u6B65\u505A\u4EC0\u4E48\uFF1F" tabindex="-1">\u4E0B\u4E00\u6B65\u505A\u4EC0\u4E48\uFF1F <a class="header-anchor" href="#\u4E0B\u4E00\u6B65\u505A\u4EC0\u4E48\uFF1F" aria-hidden="true">#</a></h2><details class="details custom-block"><summary>\u539F\u6587</summary><p>By now, you should have a basic but functional VitePress documentation site. But currently, the user has no way to navigate around the site because it&#39;s missing for example sidebar menu we have on this site.</p><p>To enable those navigations, we must add some configurations to the site. Head to <a href="https://vitepress.vuejs.org/guide/configuration.html" target="_blank" rel="noopener noreferrer">configuration guide</a> to learn how to configure VitePress.</p><p>If you would like to know more about what you can do within the page, for example, writing markdown contents, or using Vue Component, check out the &quot;Writing&quot; section of the docs. <a href="https://vitepress.vuejs.org/guide/markdown.html" target="_blank" rel="noopener noreferrer">Markdown guide</a> would be a great starting point.</p><p>If you want to know how to customize how the site looks (Theme), and find out the features VitePress&#39;s default theme provides, visit <a href="https://vitepress.vuejs.org/guide/theme-introduction.html" target="_blank" rel="noopener noreferrer">Theme: Introduction</a>.</p><p>When your documentation site starts to take shape, be sure to read the <a href="https://vitepress.vuejs.org/guide/deploying.html" target="_blank" rel="noopener noreferrer">deployment guide</a>.</p></details><p>\u73B0\u5728\uFF0C\u4F60\u5E94\u8BE5\u6709\u4E00\u4E2A\u57FA\u7840\u529F\u80FD\u7684 VitePress \u6587\u6863\u7F51\u7AD9\u3002\u4F46\u76EE\u524D\u7528\u6237\u6CA1\u6709\u529E\u6CD5\u5728\u7F51\u7AD9\u4E0A\u8FDB\u884C\u5BFC\u822A\uFF0C\u56E0\u4E3A\u5B83\u7F3A\u5C11\u4F8B\u5982\u6211\u4EEC\u5728\u8FD9\u4E2A\u7F51\u7AD9\u4E0A\u7684\u4FA7\u8FB9\u680F\u83DC\u5355\u3002</p><p>\u8981\u542F\u7528\u8FD9\u4E9B\u5BFC\u822A\u529F\u80FD\uFF0C\u6211\u4EEC\u5FC5\u987B\u5728\u7F51\u7AD9\u4E0A\u6DFB\u52A0\u4E00\u4E9B\u914D\u7F6E\u3002\u524D\u5F80<a href="/vitepressCn/configuration.html">\u914D\u7F6E\u6307\u5357</a>\uFF0C\u4E86\u89E3\u5982\u4F55\u914D\u7F6E VitePress\u3002</p><p>\u5982\u679C\u4F60\u60F3\u4E86\u89E3\u66F4\u591A\u5173\u4E8E\u4F60\u5728\u9875\u9762\u5185\u53EF\u4EE5\u505A\u7684\u4E8B\u60C5\uFF0C\u4F8B\u5982\uFF0C\u7F16\u5199 markdown \u5185\u5BB9\uFF0C\u6216\u4F7F\u7528 Vue \u7EC4\u4EF6\uFF0C\u8BF7\u67E5\u770B\u6587\u6863\u7684 &quot;Markdown &quot;\u90E8\u5206\u3002<a href="/vitepressCn/markdown.html">Markdown \u6307\u5357</a>\u5C06\u662F\u4E00\u4E2A\u5F88\u597D\u7684\u8D77\u70B9\u3002</p><p>\u5982\u679C\u4F60\u60F3\u77E5\u9053\u5982\u4F55\u5B9A\u5236\u7F51\u7AD9\u7684\u5916\u89C2\uFF08\u4E3B\u9898\uFF09\uFF0C\u5E76\u4E86\u89E3 VitePress \u7684\u9ED8\u8BA4\u4E3B\u9898\u63D0\u4F9B\u7684\u529F\u80FD\uFF0C\u8BF7\u8BBF\u95EE<a href="/vitepressCn/theme-introduction.html">\u4E3B\u9898\u4ECB\u7ECD</a>\u3002</p><p>\u5F53\u4F60\u7684\u6587\u6863\u7F51\u7AD9\u51C6\u5907\u4E0A\u7EBF\u65F6\uFF0C\u4E00\u5B9A\u8981\u9605\u8BFB<a href="/vitepressCn/deploying.html">\u90E8\u7F72\u6307\u5357</a>\u3002</p>`,35),t=[o];function l(r,c,i,d,y,D){return a(),e("div",null,t)}var h=s(p,[["render",l]]);export{u as __pageData,h as default};
